using System;using System.Reactive;using System.Reactive.Disposables;using System.Threading.Tasks;using Avalonia.Controls;using Avalonia.Dialogs;using Avalonia.ReactiveUI;using ReactiveUI;namespace lab3.Controls.MainWindow;public partial class MainWindow : ReactiveWindow<MainWindowViewModel>{    public MainWindow()    {        this.WhenActivated(d =>        {            ViewModel!.ShowOpenImageDialog                .RegisterHandler(DoShowDialog)                .DisposeWith(d);        });        InitializeComponent();    }    [Obsolete("Obsolete")]    private async Task DoShowDialog(InteractionContext<Unit, string> interactionContext)    {        var dialog = new OpenFileDialog();        var path = await dialog.ShowAsync(this);        if (path is not null)        {            interactionContext.SetOutput(path[0]);        }        else        {            interactionContext.SetOutput("../../../Assets/texture.jpg");        }    }}