using System;using System.IO;using System.Linq;using System.Reactive;using System.Reactive.Disposables;using System.Threading.Tasks;using Avalonia.Controls;using Avalonia.Dialogs;using Avalonia.ReactiveUI;using lab3.Controls.GL;using ReactiveUI;namespace lab3.Controls.MainWindow;public partial class MainWindow : ReactiveWindow<MainWindowViewModel>{    [Obsolete("Obsolete")]    public MainWindow()    {        InitializeComponent();        this.WhenActivated(d =>        {            ViewModel!.ShowOpenImageDialog                .RegisterHandler(ChooseDirectoryDialog)                .DisposeWith(d);        });    }    [Obsolete("Obsolete")]    private async Task ChooseDirectoryDialog(InteractionContext<Unit, string[]> interactionContext)    {        var openFolderDialog = new OpenFolderDialog();        var directory = await openFolderDialog.ShowAsync(this);        if (directory is not null)        {            interactionContext.SetOutput(Directory.GetFiles(directory));        }        else        {            interactionContext.SetOutput(new string[] { "../../../Assets/texture_2.png" });        }    }}