using System;using System.Reactive;using System.Reactive.Linq;using System.Xml.Serialization;using lab3.Controls.GL;using lab3.ViewModels;using ReactiveUI;using SixLabors.ImageSharp;using SixLabors.ImageSharp.PixelFormats;using SixLabors.ImageSharp.Processing;namespace lab3.Controls.MainWindow;[Serializable]public class MainWindowViewModel : ViewModelBase{    private bool _isVisibleEffectsElements = false;    private bool _isVisibleEffectBtn = true;    private bool _isVisibleRotateBtn = true;    private bool _isVisibleDrawBtn = true;    private bool _isVisibleSaveBtn = true;    private bool _blackAndWhiteFilter = false;    private bool _blueFilter = false;    private bool _greenFilter = false;    private bool _redFilter = false;    private bool _isCheckedBW = false;    private bool _isCheckedR = false;    private bool _isCheckedG = false;    private bool _isCheckedB = false;    private BackUpSerializer _serializer;    [field: NonSerialized] [XmlIgnore] private ImgBitmap _img;    public ReactiveCommand<Unit, Unit> Save { get; }    public readonly Interaction<Unit, string> ShowOpenImageDialog;    public ReactiveCommand<Unit, Unit> OpenImage { get; }    private string _pathToPicture;    public string PathToPicture    {        get => _pathToPicture;        set => this.RaiseAndSetIfChanged(ref _pathToPicture, value);    }    public void SetPicture()    {        var img = Image.Load<Rgba32>(PathToPicture);        img.Mutate(context => context.Rotate((RotateMode)_rotateMode));        var pixels = new byte[img.Width * 4 * img.Height];        img.CopyPixelDataTo(pixels);        Img = new ImgBitmap(img.Width, img.Height, pixels);        img.Dispose();    }    public int _rotateMode { get; set; }    public MainWindowViewModel()    {        _rotateMode =  (int)RotateMode.None;        _serializer = new BackUpSerializer("../../../BackUp.xml");        Rotate = ReactiveCommand.Create(DoRotation);        PathToPicture = new string("../../../Assets/texture.jpg");        SetPicture();        ShowOpenImageDialog = new Interaction<Unit, string>();        OpenImage = ReactiveCommand.CreateFromTask(async () =>        {            var path = await ShowOpenImageDialog.Handle(Unit.Default);            if (path is not null)            {                _rotateMode = (int)RotateMode.None;                PathToPicture = path;                SetPicture();            }        });        PathToPicture = new string("../../../Assets/texture.jpg");        EnableEffects = ReactiveCommand.Create(() =>        {            BlueFilter = IsCheckedB;            GreenFilter = IsCheckedG;            RedFilter = IsCheckedR;            BlackAndWhiteFilter = IsCheckedBW;        });        OpenEffects = ReactiveCommand.Create(() =>        {            IsVisibleEffectsElements = true;            IsVisibleSaveBtn = false;            IsVisibleDrawBtn = false;            IsVisibleEffectBtn = false;            IsVisibleRotateBtn = false;        });        BackFromEffects = ReactiveCommand.Create(() =>        {            IsVisibleEffectsElements = false;            IsVisibleSaveBtn = true;            IsVisibleDrawBtn = true;            IsVisibleEffectBtn = true;            IsVisibleRotateBtn = true;        });    }    private void DoRotation()    {        _rotateMode = (RotateMode)_rotateMode switch        {            RotateMode.None => (int)RotateMode.Rotate270,            RotateMode.Rotate270 => (int)RotateMode.Rotate180,            RotateMode.Rotate180 => (int)RotateMode.Rotate90,            RotateMode.Rotate90 => (int)RotateMode.None,            _ => throw new ArgumentOutOfRangeException()        };        SetPicture();    }    public ImgBitmap Img    {        get => _img;        set        {            this.RaiseAndSetIfChanged(ref _img, value);            _serializer.BackUp(this);        }    }    public bool IsCheckedB    {        get => _isCheckedB;        set        {            this.RaiseAndSetIfChanged(ref _isCheckedB, value);            _serializer.BackUp(this);        }    }    public bool IsCheckedG    {        get => _isCheckedG;        set        {            this.RaiseAndSetIfChanged(ref _isCheckedG, value);            _serializer.BackUp(this);        }    }    public bool IsCheckedR    {        get => _isCheckedR;        set        {            this.RaiseAndSetIfChanged(ref _isCheckedR, value);            _serializer.BackUp(this);        }    }    public bool IsCheckedBW    {        get => _isCheckedBW;        set        {            this.RaiseAndSetIfChanged(ref _isCheckedBW, value);            _serializer.BackUp(this);        }    }    public bool BlackAndWhiteFilter    {        get => _blackAndWhiteFilter;        set        {            this.RaiseAndSetIfChanged(ref _blackAndWhiteFilter, value);            _serializer.BackUp(this);        }    }    public bool GreenFilter    {        get => _greenFilter;        set        {            this.RaiseAndSetIfChanged(ref _greenFilter, value);            _serializer.BackUp(this);        }    }    public bool RedFilter    {        get => _redFilter;        set        {            this.RaiseAndSetIfChanged(ref _redFilter, value);            _serializer.BackUp(this);        }    }    public bool BlueFilter    {        get => _blueFilter;        set        {            this.RaiseAndSetIfChanged(ref _blueFilter, value);            _serializer.BackUp(this);        }    }    public bool IsVisibleEffectBtn    {        get => _isVisibleEffectBtn;        set        {            this.RaiseAndSetIfChanged(ref _isVisibleEffectBtn, value);            _serializer.BackUp(this);        }    }    public bool IsVisibleSaveBtn    {        get => _isVisibleSaveBtn;        set        {            this.RaiseAndSetIfChanged(ref _isVisibleSaveBtn, value);            _serializer.BackUp(this);        }    }    public bool IsVisibleRotateBtn    {        get => _isVisibleRotateBtn;        set        {            this.RaiseAndSetIfChanged(ref _isVisibleRotateBtn, value);            _serializer.BackUp(this);        }    }    public bool IsVisibleDrawBtn    {        get => _isVisibleDrawBtn;        set        {            this.RaiseAndSetIfChanged(ref _isVisibleDrawBtn, value);            _serializer.BackUp(this);        }    }    public bool IsVisibleEffectsElements    {        get => _isVisibleEffectsElements;        set        {            this.RaiseAndSetIfChanged(ref _isVisibleEffectsElements, value);            _serializer.BackUp(this);        }    }    public ReactiveCommand<Unit, Unit> OpenEffects { get; }    public ReactiveCommand<Unit, Unit> EnableEffects { get; }    public ReactiveCommand<Unit, Unit> BackFromEffects { get; }    public ReactiveCommand<Unit, Unit> Rotate { get; }}