using System;using Avalonia;using Avalonia.ReactiveUI;namespace lab3;internal class Program{    // Initialization code. Don't use any Avalonia, third-party APIs or any    // SynchronizationContext-reliant code before AppMain is called: things aren't initialized    // yet and stuff might break.    [STAThread]    public static void Main(string[] args)    {        BuildAvaloniaApp()            .StartWithClassicDesktopLifetime(args);    }    public static AppBuilder BuildAvaloniaApp()    {        return AppBuilder.Configure<App>()            .UsePlatformDetect()            .LogToTrace()            .UseReactiveUI()            .With(new Win32PlatformOptions            {                UseWgl = true,                AllowEglInitialization = true            });    }}